// pages/home/home.js
import { request } from './../../utils/index.js'
Page({

  /**
   * 页面的初始数据
   */
  data: {
    bannerlist: [],
    prolist: [],
    pageCode: 1 // 页码
  },

  /**
   * 生命周期函数--监听页面加载
   */
  onLoad: function (options) {
    request('/api/pro/banner').then(data => {
      console.log(data)
      this.setData({
        bannerlist: data.data
      })
    })

    request('/api/pro').then(data => {
      console.log(data)
      this.setData({
        prolist: data.data
      })
    })
  },

  /**
   * 页面相关事件处理函数--监听用户下拉动作
   */
  onPullDownRefresh: function () {
    request('/api/pro').then(data => {
      console.log(data)
      this.setData({
        prolist: data.data,
        pageCode: 1
      })
    })
  },

  /**
   * 页面上拉触底事件的处理函数
   */
  onReachBottom: function () {
    let num = this.data.pageCode;
    let prolist = this.data.prolist
    num++;
    console.log(num)
    request('/api/pro', {
      pageCode: num
    }).then(data => {
      // 此处注意临界值的变化 --  没有数据
      this.setData({
        prolist: [...prolist, ...data.data],
        pageCode: num
      })
    })
  }
})
